import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../../core/services/routes.dart';import '../../../../../presentationUser/common/common_state.dart';import '../../../../usersManagement/domain/entities/company.dart';import '../../../data/models/add_edit_classification_prams.dart';import '../../../domain/entities/classification.dart';import '../bloc/classification_cubit.dart';import 'add_classification_screen.dart';class AddClassificationPage extends BaseBlocWidget<Initialized<List<Company>>, AddClassificationCubit> {  static push(BuildContext context, {required Function() onSuccess, Classification? classification}) async {    final result = await Navigator.pushNamed(context, Routes.addClassificationPage, arguments: classification);    if (result is bool && result) {      print('push: $result');      onSuccess();    }  }  @override  bool detectRequiredTasks() {    return false;  }  @override  void loadInitialData(BuildContext context) {    bloc.fetchInitialData();  }  @override  String? title(BuildContext context) {    final initialData = (ModalRoute.of(context)?.settings.arguments ?? Classification()) as Classification;    return initialData.id == 0 || initialData.id == null ? strings.add_classification : strings.edit_classification;  }  @override  Widget buildWidget(BuildContext context, Initialized<List<Company>> state) {    final initialData = getArguments(context);    return AddClassificationScreen(      company: state.data,      classification: initialData,      onAddEdit: (AddEditClassificationPrams addEditClassificationPrams) {        //  print(object)        bloc.addEditClassification(addEditClassificationPrams: addEditClassificationPrams);      },    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }}