import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../presentationUser/common/common_state.dart';import '../../../../projectsManagement/domain/entities/city.dart';import '../../../domain/entities/employment_official.dart';import '../bloc/add_employment_official_bloc.dart';import 'add_employment_official_screen.dart';class AddEmploymentOfficialPage    extends BaseBlocWidget<Initialized<List<City>>, AddEmploymentOfficialCubit> {  AddEmploymentOfficialPage({Key? key}) : super(key: key);  @override  void loadInitialData(BuildContext context) {    bloc.fetchAllaData();  }  @override  bool initializeByKoin() {    return false;  }  @override  bool detectRequiredTasks() {    return false;  }  @override  String? title(BuildContext context) {    return getArguments(context) == null        ? strings.add_employment_official        : strings.edit_employment_official_data;  }  @override  Widget buildWidget(BuildContext context, Initialized<List<City>> state) {    EmploymentOfficial? initialData =  getArguments(context);    return AddEmploymentOfficialScreen(        cities: state.data,        initialData: initialData,        onAddEmploymentOfficial: (params) {          bloc.addEmploymentOfficial(params);        });  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }}