import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/presentationUser/common/common_state.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../domain/entities/GasStation.dart';import '../bloc/add_gas_stations_bloc.dart';import 'add_gas_stations_screen.dart';class AddGasStationsPage    extends BaseBlocWidget<InitializedGasStations, AddGasStationsCubit> {  @override  String? title(BuildContext context) {    return strings.add_gas_stations;  }  @override  void loadInitialData(BuildContext context) {    GasStation? gasStation = getArguments(context);    if (gasStation != null) {      bloc.fetchProjectsCompanyId(gasStation.companyId??0);    }    bloc.fetchData();  }  @override  Widget buildWidget(BuildContext context, InitializedGasStations state) {    return AddGasStationsScreen(      company: state.companies,      projectsStream: bloc.projectsStream,      onFetchProject: (companyId) => bloc.fetchProjectsCompanyId(companyId),      addEditGasStation: (params) => bloc.addEditGasStation(params),      gasStation: getArguments(context),    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }  @override  bool detectRequiredTasks() {    return false;  }}