import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../../extensions/extensions.dart';import '../../../../../presentationUser/common/common_state.dart';import '../../../domain/entities/user_management.dart';import '../bloc/change_user_password_bloc.dart';import 'change_user_password_screen.dart';class ChangeUserPasswordPage extends BaseBlocWidget<UnInitState, ChangeUserPasswordCubit> {  ChangeUserPasswordPage({Key? key}) : super(key: key);  @override  bool initializeByKoin() {    return false;  }  @override  bool detectRequiredTasks() {    return false;  }  @override  String? title(BuildContext context) {    return strings.edit_password;  }  @override  Widget buildWidget(      BuildContext context, UnInitState state) {    final settings = MyModalRoute.of(context)?.settings;    UserManagement? initialData = settings?.arguments == null        ? UserManagement()        : settings?.arguments as UserManagement?;    return ChangeUserPasswordScreen(        userInfo: initialData,        changePassword: (params){        bloc.changePassword(params);      }    );  }  @override  void onRequestSuccess(String? message) {    Navigator.pop(context);    super.onRequestSuccess(message);  }}