import 'package:injectable/injectable.dart';import 'package:shiftapp/core/bloc/base_cubit.dart';import 'package:shiftapp/presentation/presentationUser/common/common_state.dart';import '../../data/models/reject_user_request_params.dart';import '../../data/models/user_request_terms_params.dart';import '../../data/repositories/users_requests_repository.dart';@Injectable()class UserRequestTermsCubit extends BaseCubit {  final UsersRequestsRepository _repository;  UserRequestTermsCubit(    this._repository,  );  void fetchUserRequestTerms(UserRequestTermsParams params) {    executeEmitterData(() => _repository.fetchUserRequestTerms(params));  }  void acceptRejectUserRequest(RejectUserRequestParams params) {    executeEmitterListener(() => _repository.acceptRejectUserRequest(params));  }}