import '../../../../shared/components/index.dart';import '../../../../shared/models/common_list_item.dart';import '../../data/models/reject_user_request_params.dart';import '../../domain/entities/user_request.dart';import '../widgets/user_request_item.dart';class UsersRequestsScreen extends BaseStatelessWidget {  final List<UserRequest> data;  final Function(RejectUserRequestParams) onReject;  final Function() onRefresh;  final CommonListItem? selectedTab;  UsersRequestsScreen(      {super.key,      required this.data,      required this.onReject,      required this.onRefresh,      this.selectedTab,      });  @override  Widget build(BuildContext context) {    return ListView.builder(      itemCount: data.length,      itemBuilder: (context, index) => UserRequestItem(        data: data[index],        onReject: onReject,        onRefresh: onRefresh,          selectedTab: selectedTab,      ),    );  }}