import 'package:injectable/injectable.dart';import 'package:shiftapp/core/bloc/base_cubit.dart';import '../../../../data/models/index.dart';@Injectable()class AddVehicleCameraCubit extends BaseCubit {  final VehiclesRepository _repository;  AddVehicleCameraCubit(this._repository);  void addEditVehicleViolation(AddVehicleCameraParams params) {    executeEmitterListener(      () => params.id == null          ? _repository.addVehicleCamera(params)          : _repository.editVehicleCamera(params),    );  }}