import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../../presentationUser/common/common_state.dart';import '../../../../data/models/add_vehicle_camera_params.dart';import '../../../../domain/entities/vehicle_camera.dart';import '../bloc/add_vehicle_camera_bloc.dart';import 'add_vehicle_camera_screen.dart';class AddVehicleCameraPage    extends BaseBlocWidget<UnInitState, AddVehicleCameraCubit> {  AddVehicleCameraPage({super.key});  @override  bool detectRequiredTasks() {    return false;  }  VehicleCamera? initialData = null;  @override  String? title(BuildContext context) {    initialData = getArguments(context);    return initialData?.id == null        ? strings.add_camera        : strings.edit_camera;  }  @override  Widget buildWidget(BuildContext context, UnInitState state) {    return AddVehicleCameraScreen(      initialData: initialData,      onAddEdit: (AddVehicleCameraParams params) {        bloc.addEditVehicleViolation(params);      },    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }}