import 'package:flutter/cupertino.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../../presentationUser/common/common_state.dart';import '../../../../data/models/vehicle_video_params.dart';import '../../../../domain/entities/camera_search_result_args.dart';import '../bloc/camera_search_results_cubit.dart';import 'camera_search_results_screen.dart';class CameraSearchResultsPage    extends BaseBlocWidget<UnInitState, CameraSearchResultsCubit> {  CameraSearchResultsPage({Key? key}) : super(key: key);  @override  String? title(BuildContext context) {    return strings.cameras;  }  int vehicleId = 0;  CameraSearchResultArgs? cameraSearchResultArgs;  @override  loadInitialData(BuildContext context) {    cameraSearchResultArgs = getArguments(context);    print('CameraSearchResultArgs: $cameraSearchResultArgs');    bloc.fetchVehicleCameras(cameraSearchResultArgs!);  }  @override  bool detectRequiredTasks() {    return false;  }  @override  Widget buildWidget(BuildContext context, UnInitState state) {    return CameraSearchResultsScreen(      dataStream: bloc.cameraSearchResultsStream,      args: cameraSearchResultArgs!,      onFilter: (VehicleVideoParams vehicleVideoParams) {        cameraSearchResultArgs?.vehicleVideoParams = vehicleVideoParams;        bloc.fetchVehicleCameras(cameraSearchResultArgs!);      },    );  }  @override  void onSuccessDismissed() {    loadInitialData(context);  }}