import 'package:shiftapp/presentation/adminFeatures/vehicles/domain/entities/index.dart';import '../../../../../../shared/components/index.dart';import '../../../../domain/entities/vehicle_camera.dart';import '../intent/vehicle_cameras_intents.dart';import '../widgets/vehicle_camera_item.dart';class VehicleCamerasScreen extends BaseStatelessWidget {  final List<VehicleCamera> data;  final Function(VehicleCamerasIntents) onActionIntent;  VehicleCamerasScreen({    super.key,    required this.data,    required this.onActionIntent,  });  @override  Widget build(BuildContext context) {    return ListView.builder(      itemCount: data.length,      padding: EdgeInsets.symmetric(vertical: 8),      itemBuilder: (context, index) => VehicleCameraItem(        item: data[index],        onActionIntent: onActionIntent,      ),    );  }}