import 'package:google_maps_flutter/google_maps_flutter.dart';import 'package:shiftapp/presentation/adminFeatures/vehicles/presentation/vehicleTracking/widgets/item_traking.dart';import 'package:shiftapp/presentation/shared/components/index.dart';import '../../../domain/entities/vehicleTraking.dart';import '../widgets/animationCarRouteWidget.dart';class VehiclesTrackingScreen extends BaseStatelessWidget {  VehicleTrackingEntity? vehicleTracingDetails;  VehiclesTrackingScreen({super.key, required this.vehicleTracingDetails});  GoogleMapController? _mapController;  @override  Widget build(BuildContext context) {    return Stack(      children: [        AnimationCarRouteWidget(          points: vehicleTracingDetails?.vehicleGPSDetails?.details ?? [],        ),        if (  vehicleTracingDetails?.vehicleEvents!=null )          Positioned(              top: 110,              left: 20,              right: 20,              child: ItemTracking(                vehicleTrackingDetailsEntity: vehicleTracingDetails,              )),      ],    );  }}