import 'dart:io';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../../presentationUser/common/common_state.dart';import '../../../../../../presentationUser/vehiclesOperation/domain/entities/vehicle_violation_args.dart';import '../../../../data/models/add_vehicle_violation_params.dart';import '../bloc/add_vehicle_violation_bloc.dart';import '../bloc/add_vehicle_violation_state.dart';import 'add_violation_vehicle_screen.dart';class AddVehicleViolationPage extends BaseBlocWidget<Initialized<AddVehicleViolationState>,    AddVehicleViolationCubit> {  AddVehicleViolationPage({super.key});  @override  void loadInitialData(BuildContext context) {    AddVehicleViolationArgs? args = getArguments(context);    bloc.fetchInitialData(args?.violationId);  }  @override  bool detectRequiredTasks() {    return false;  }  @override  String? title(BuildContext context) {    AddVehicleViolationArgs? args = getArguments(context);    return args?.violationId == null        ? strings.add_violation        : strings.edit_violation;  }  @override  Widget buildWidget(      BuildContext context, Initialized<AddVehicleViolationState> state) {    return AddVehicleViolationScreen(      state: state.data,      onAddVehicleViolation: (AddVehicleViolationParams params, List<File> files) {        AddVehicleViolationArgs? args = getArguments(context);        params.roundTripId = args?.roundTripId;        bloc.addVehicleViolation(params, files: files);      },    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }}