import '../../../../../../../shared/components/index.dart';import '../../../../../domain/entities/vehicle_violation.dart';import '../../widgets/vehicle_violation_item.dart';class VehicleViolationsScreen extends BaseStatelessWidget {  final List<ContractViolation> data;  final Function() onRefresh;  bool       isPlan;  VehicleViolationsScreen({super.key, required this.isPlan, required this.data, required this.onRefresh});  @override  Widget build(BuildContext context) {    return ListView.builder(      itemCount: data.length,      itemBuilder: (context, index) =>          VehicleViolationItem(item: data[index],       isPlan:isPlan, onRefresh: onRefresh),    );  }}