import 'package:shiftapp/presentation/presentationUser/common/common_state.dart';import 'package:shiftapp/presentation/presentationUser/transactions/presentation/view/pages/transactions_screen.dart';import 'package:shiftapp/presentation/shared/components/text_field/date_time_text_field_picker.dart';import '../../../../../../../main_index.dart';import '../../../../../shared/components/index.dart';import '../../../../advancedFilter/widgets/text_field_search_job.dart';import '../../../../salarydefinitionrequest/requests/widgets/title_and_add_new_request.dart';import '../cubit/add_transactions_cubit.dart';import 'add_transactions_screen.dart';class AddTransactionsPage    extends BaseBlocWidget<UnInitState, AddTransactionsCubit> {  @override  String? title(BuildContext context) {    // TODO: implement title    return strings.add_transactions;  }  static push(BuildContext context, {required Function() onSuccess}) async {    final result = await Navigator.pushNamed(      context,      Routes.addTransactionsPage,    );    if (result is bool && result) {      onSuccess();    }  }  @override  Widget buildWidget(BuildContext context, UnInitState state) {    return AddTransactionsScreen(      onAddTransactions: (params) => bloc.addTransactions(params),    );  }  @override  void onSuccessDismissed() {    Navigator.pop(context, true);  }}