import 'package:google_maps_flutter/google_maps_flutter.dart';import 'package:shiftapp/presentation/shared/components/index.dart';import '../../../data/models/reschedule_transactions_prams.dart';import '../../../data/models/update_transactions_prams.dart';import '../../../domain/entities/transaction_data.dart';import '../widgets/transactions_item.dart';class TransactionsScreen extends BaseStatelessWidget {  final List<Transactions> data;  Function(UpdateTransactionsPrams update) onUpdate;  Function(RescheduleTransactionsPrams update) onReschedule;  TransactionsScreen({    required this.data,    required this.onUpdate,    required this.onReschedule,  });  @override  Widget build(BuildContext context) {    return ListView.builder(      itemCount: data.length,      itemBuilder: (context, index) {        return TransactionsItem(          data: data[index],          onReschedule: onReschedule,          onUpdate: onUpdate,        );      },    );  }}