import 'package:flutter/cupertino.dart';import 'package:shiftapp/presentation/adminFeatures/vehicles/domain/entities/index.dart';import 'package:shiftapp/presentation/adminFeatures/vehicles/presentation/violations/view/pages/vehicle/vehicle_violations_screen.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import 'package:shiftapp/presentation/presentationUser/common/common_state.dart';import '../bloc/factory_violations_plane_cubit.dart';class FactoryViolationsPlanePage    extends        BaseBlocWidget<          Initialized<ContractViolationData>,          FactoryViolationsPlaneCubit        > {  @override  String? title(BuildContext context) {    return strings.violations;  }  @override  void loadInitialData(BuildContext context) {    bloc.fetchInitialData(getArguments(context));  }  @override  Widget buildWidget(    BuildContext context,    Initialized<ContractViolationData> state,  ) {    return VehicleViolationsScreen(      isPlan: true,      data: state.data.contractViolations ?? [],      onRefresh: () {        loadInitialData(context);      },    );  }}