import 'package:shiftapp/presentation/presentationUser/common/common_state.dart';import 'package:shiftapp/presentation/presentationUser/vehiclesOperation/presentation/currentTourMap/tour_history/pages/tour_history_screen.dart';import 'package:shiftapp/presentation/shared/components/base_widget_bloc.dart';import '../../../../../../../main_index.dart';import '../../../../../common/stream_data_state.dart';import '../../../../domain/entities/index.dart';import '../../chnage_operating_plane/bloc/tour_history_cubit.dart';class TourHistoryPage    extends BaseBlocWidget<Initialized<List<RoundTripDetails>>, TourHistoryCubit> {  final CurrentRoundTrip currentRoundTrip;  TourHistoryPage({super.key, required this.currentRoundTrip});  @override  void loadInitialData(BuildContext context) {    bloc.fetchInitialData(currentRoundTrip.id ?? 0);  }  @override  Widget buildWidget(BuildContext context, Initialized<List<RoundTripDetails>> state) {    return TourHistoryScreen(      data: state.data,    );  }  @override  void onSuccessDismissed() {    Navigator.pushNamed(context, Routes.currentTourMapPage);  }}